import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseAnonKey) {
  throw new Error('Missing Supabase environment variables');
}

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

// Test connection and log status
supabase.from('hira_projects')
  .select('count', { count: 'exact', head: true })
  .then(({ error }) => {
    if (error) {
      console.error('Supabase connection error:', error.message);
    } else {
      console.log('Successfully connected to Supabase');
    }
  })
  .catch(err => {
    console.error('Failed to test Supabase connection:', err.message);
  });

// Constants for file upload limits
const MAX_FILES = 5;
const MAX_TOTAL_SIZE = 50 * 1024 * 1024; // 50MB in bytes
const BUCKET_NAME = 'hira-files';
const SUBFOLDER = 'operational-images';

// Helper function to list files in storage
export const listFiles = async () => {
  try {
    const { data, error } = await supabase.storage
      .from(BUCKET_NAME)
      .list(SUBFOLDER);

    if (error) {
      console.error('Error listing files:', error);
      throw error;
    }

    // Get public URLs for each file
    const filesWithUrls = await Promise.all(data.map(async (file) => {
      const { data: { publicUrl } } = supabase.storage
        .from(BUCKET_NAME)
        .getPublicUrl(`${SUBFOLDER}/${file.name}`);

      return {
        name: file.name,
        size: file.metadata?.size,
        path: `${SUBFOLDER}/${file.name}`,
        url: publicUrl,
        type: file.metadata?.mimetype
      };
    }));

    return filesWithUrls;
  } catch (error) {
    console.error('Error listing files:', error);
    throw error;
  }
};

// Helper function to validate files
export const validateFiles = (newFiles, existingFiles = []) => {
  console.log('Validating files:', { 
    newFiles: newFiles.map(f => ({ name: f.name, size: f.size })),
    existingFiles: existingFiles.map(f => ({ name: f.name, size: f.size }))
  });

  const totalFiles = existingFiles.length + newFiles.length;
  if (totalFiles > MAX_FILES) {
    console.error('File count validation failed:', { totalFiles, MAX_FILES });
    throw new Error(`Maximum ${MAX_FILES} files allowed`);
  }

  const existingSize = existingFiles.reduce((total, file) => total + (file.size || 0), 0);
  const newSize = newFiles.reduce((total, file) => total + file.size, 0);
  const totalSize = existingSize + newSize;

  console.log('Size validation:', { 
    existingSize: formatFileSize(existingSize),
    newSize: formatFileSize(newSize),
    totalSize: formatFileSize(totalSize),
    maxSize: formatFileSize(MAX_TOTAL_SIZE)
  });

  if (totalSize > MAX_TOTAL_SIZE) {
    console.error('File size validation failed:', { totalSize, MAX_TOTAL_SIZE });
    throw new Error(`Total file size cannot exceed 50MB`);
  }

  console.log('File validation passed');
  return true;
};

// Helper functions for common operations
export const uploadFile = async (file, existingFiles = []) => {
  console.log('Starting file upload:', { 
    fileName: file.name,
    fileSize: formatFileSize(file.size),
    fileType: file.type
  });

  try {
    // Validate the new file
    validateFiles([file], existingFiles);

    const fileExt = file.name.split('.').pop();
    const fileName = `${Date.now()}.${fileExt}`;
    const filePath = `${SUBFOLDER}/${fileName}`;

    console.log('Uploading file to Supabase:', { 
      bucket: BUCKET_NAME,
      filePath,
      fileSize: formatFileSize(file.size)
    });

    // Upload the file
    const { data, error } = await supabase.storage
      .from(BUCKET_NAME)
      .upload(filePath, file, {
        cacheControl: '3600',
        upsert: false
      });

    if (error) {
      console.error('Supabase upload error:', error);
      throw error;
    }

    console.log('File uploaded successfully:', { filePath, data });

    // Get the public URL
    const { data: { publicUrl } } = supabase.storage
      .from(BUCKET_NAME)
      .getPublicUrl(filePath);

    console.log('Generated public URL:', publicUrl);

    return {
      url: publicUrl,
      path: filePath,
      name: file.name,
      size: file.size,
      type: file.type
    };
  } catch (error) {
    console.error('File upload error:', error);
    throw error;
  }
};

export const deleteFile = async (path) => {
  console.log('Attempting to delete file:', path);

  try {
    if (!path.startsWith(SUBFOLDER)) {
      path = `${SUBFOLDER}/${path}`;
    }

    console.log('Deleting file from Supabase:', { bucket: BUCKET_NAME, path });

    const { error } = await supabase.storage
      .from(BUCKET_NAME)
      .remove([path]);

    if (error) {
      console.error('Supabase delete error:', error);
      throw error;
    }

    console.log('File deleted successfully');
  } catch (error) {
    console.error('File deletion error:', error);
    throw error;
  }
};

// Helper function to get file size in readable format
export const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};
