export default function HazardIdentification() {
  const { 
    currentProject,
    hazardIdentificationData,
    setStepData,
    saveStepData,
    setCurrentStep,
    error: storeError,
    isLoading
  } = useProjectStore();

  // Local state for component-specific errors
  const [localError, setLocalError] = useState(null);

  // Create default event structure
  const defaultEvent = {
    uniqueId: uuidv4(),
    name: '',
    hazards: [{
      uniqueId: uuidv4(),
      description: '',
      consequences: [{
        uniqueId: uuidv4(),
        description: '',
        currentControls: ''
      }]
    }]
  };

  // Get initial events from store or use default
  const initialEvents = hazardIdentificationData?.events || [defaultEvent];

  const { register, control, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      events: initialEvents
    }
  });

  const { fields: events, append: appendEvent, remove: removeEvent } = useFieldArray({
    control,
    name: "events"
  });

  const handleFormSubmit = async (data) => {
    try {
      setLocalError(null);

      // Check if we have a current project
      if (!currentProject?.id) {
        throw new Error('No active project found. Please ensure the project is created first.');
      }

      console.log('Current project:', currentProject); // Debug log

      // Format the data
      const formattedData = {
        project_id: currentProject.id,
        events: data.events.map(event => ({
          uniqueId: event.uniqueId || uuidv4(),
          name: event.name,
          hazards: event.hazards.map(hazard => ({
            uniqueId: hazard.uniqueId || uuidv4(),
            description: hazard.description,
            consequences: hazard.consequences.map(consequence => ({
              uniqueId: consequence.uniqueId || uuidv4(),
              description: consequence.description,
              currentControls: consequence.currentControls || ''
            }))
          }))
        }))
      };

      // Save to store
      console.log('Saving to store...'); // Debug log
      const saved = await setStepData(2, formattedData);
      console.log('Store save result:', saved); // Debug log

      if (!saved) {
        throw new Error('Failed to save hazard identification data to store');
      }

      // Save to database (original method)
      console.log('Saving to database...'); // Debug log
      const savedToDb = await saveStepData(2);
      console.log('Database save result:', savedToDb); // Debug log

      if (!savedToDb) {
        throw new Error('Failed to save hazard identification data to database');
      }

      // Also save to relational tables
      try {
        console.log('Saving to relational tables...'); // Debug log
        for (const event of data.events) {
          await projectService.saveEvent(currentProject.id, event);
        }
        console.log('Saved to relational tables successfully'); // Debug log
      } catch (err) {
        console.error('Error saving to relational tables:', err);
        // Don't throw here as we've already saved to the original tables
      }

      // Proceed to next step
      setCurrentStep(3);
    } catch (err) {
      console.error('Error saving hazard identification:', err);
      setLocalError(err.message);
    }
  };

  // Show loading state
  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  // Check if we have a current project
  if (!currentProject?.id) {
    return (
      <div className="space-y-6 max-w-6xl mx-auto p-6">
        <div className="bg-white shadow-md rounded-lg p-6">
          <p className="text-center text-red-600">
            No active project found. Please ensure the project is created first.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6 max-w-6xl mx-auto p-6">
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Hazard Identification</h2>

        {(storeError || localError) && (
          <ErrorAlert message={localError || storeError} />
        )}

        {Object.keys(errors).length > 0 && (
          <ErrorAlert message="Please fill in all required fields" />
        )}

        <form onSubmit={handleSubmit(handleFormSubmit)}>
          <div className="space-y-8">
            {events.map((event, eventIndex) => (
              <HazardEvent
                key={event.uniqueId || eventIndex}
                eventIndex={eventIndex}
                register={register}
                control={control}
                removeEvent={removeEvent}
              />
            ))}

            <div className="flex justify-between">
              <button
                type="button"
                onClick={() => appendEvent(defaultEvent)}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-blue-700 bg-blue-100 hover:bg-blue-200"
              >
                <Plus className="h-4 w-4 mr-2" />
                Add Safety Event
              </button>

              <button
                type="submit"
                disabled={isLoading}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isLoading ? 'Saving...' : 'Next Step'}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

