  onSubmit: PropTypes.func,
  initialData: PropTypes.shape({
    events: PropTypes.arrayOf(PropTypes.shape({
      uniqueId: PropTypes.string,
      name: PropTypes.string,
      hazards: PropTypes.arrayOf(PropTypes.shape({
        uniqueId: PropTypes.string,
