import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseAnonKey) {
  throw new Error('Missing Supabase environment variables');
}

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

// Constants for file upload limits
const MAX_FILES = 5;
const MAX_TOTAL_SIZE = 50 * 1024 * 1024; // 50MB in bytes
const BUCKET_NAME = 'hira-files';
const SUBFOLDER = 'operational-images';

// Helper function to get project-specific folder path
const getProjectFolder = (project_id) => `${SUBFOLDER}/${project_id}`;

// Helper function to list files in storage for a specific project
export const listFiles = async (project_id) => {
  if (!project_id) {
    console.error('Project ID is required to list files');
    return [];
  }

  try {
    console.log('Listing files for project:', project_id);
    const projectFolder = getProjectFolder(project_id);
    
    const { data, error } = await supabase.storage
      .from(BUCKET_NAME)
      .list(projectFolder);

    if (error) {
      console.error('Error listing files:', error);
      return [];
    }

    if (!data) {
      console.log('No files found for project:', project_id);
      return [];
    }

    console.log('Files found:', data);

    // Get public URLs for project files
    const filesWithUrls = await Promise.all(data.map(async (file) => {
      const filePath = `${projectFolder}/${file.name}`;
      const { data: { publicUrl } } = supabase.storage
        .from(BUCKET_NAME)
        .getPublicUrl(filePath);

      return {
        name: file.name.replace(`${project_id}_`, ''), // Remove project_id prefix from display name
        size: file.metadata?.size,
        path: filePath,
        url: publicUrl,
        type: file.metadata?.mimetype,
        project_id
      };
    }));

    console.log('Files with URLs:', filesWithUrls);
    return filesWithUrls;
  } catch (error) {
    console.error('Error listing files:', error);
    return [];
  }
};

// Helper function to validate files
export const validateFiles = (newFiles, existingFiles = []) => {
  const totalFiles = existingFiles.length + newFiles.length;
  if (totalFiles > MAX_FILES) {
    throw new Error(`Maximum ${MAX_FILES} files allowed`);
  }

  const existingSize = existingFiles.reduce((total, file) => total + (file.size || 0), 0);
  const newSize = newFiles.reduce((total, file) => total + file.size, 0);
  const totalSize = existingSize + newSize;

  if (totalSize > MAX_TOTAL_SIZE) {
    throw new Error(`Total file size cannot exceed 50MB`);
  }

  return true;
};

// Helper function to upload file
export const uploadFile = async (file, project_id, existingFiles = []) => {
  if (!project_id) {
    throw new Error('Project ID is required to upload files');
  }

  try {
    validateFiles([file], existingFiles);

    const fileExt = file.name.split('.').pop();
    const fileName = `${project_id}_${Date.now()}.${fileExt}`;
    const filePath = `${getProjectFolder(project_id)}/${fileName}`;

    console.log('Uploading file:', {
      originalName: file.name,
      fileName,
      filePath,
      project_id
    });

    const { data, error } = await supabase.storage
      .from(BUCKET_NAME)
      .upload(filePath, file, {
        cacheControl: '3600',
        upsert: false
      });

    if (error) throw error;

    const { data: { publicUrl } } = supabase.storage
      .from(BUCKET_NAME)
      .getPublicUrl(filePath);

    return {
      url: publicUrl,
      path: filePath,
      name: file.name, // Keep original name for display
      size: file.size,
      type: file.type,
      project_id
    };
  } catch (error) {
    console.error('File upload error:', error);
    throw error;
  }
};

// Helper function to delete file
export const deleteFile = async (path, project_id) => {
  if (!project_id) {
    console.error('Project ID is required to delete files');
    return;
  }

  try {
    console.log('Deleting file:', { path, project_id });

    const { error } = await supabase.storage
      .from(BUCKET_NAME)
      .remove([path]);

    if (error) throw error;

    console.log('File deleted successfully');
  } catch (error) {
    console.error('File deletion error:', error);
    throw error;
  }
};

// Helper function to get file size in readable format
export const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};
