import React from 'react';
import { useForm } from 'react-hook-form';
import RiskMatrixSelector from './RiskMatrixSelector';
import RiskMatrixReference from './RiskMatrixReference';
import AssessmentForm from './AssessmentForm';
import useProjectStore from '../stores/projectStore';

// Error Alert Component
const ErrorAlert = ({ message }) => {
  if (!message) return null;

  return (
    <div className="p-4 mb-4 bg-red-50 border border-red-200 rounded-lg">
      <div className="flex items-center">
        <div className="flex-shrink-0">
          <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
          </svg>
        </div>
        <div className="ml-3">
          <p className="text-sm text-red-700">{message}</p>
        </div>
      </div>
    </div>
  );
};

// Loading Spinner Component
const LoadingSpinner = () => (
  <div className="flex justify-center items-center h-64">
    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
  </div>
);

export default function RiskAssessment() {
  const {
    hazardIdentificationData,
    riskAssessmentData,
    setStepData,
    saveStepData,
    setCurrentStep,
    error,
    isLoading
  } = useProjectStore();

  // Ensure we have hazard identification data
  const events = hazardIdentificationData?.events || [];

  // Initialize form with existing data or create new assessments from hazards
  const { register, handleSubmit, watch, setValue, formState: { errors } } = useForm({
    defaultValues: {
      assessments: riskAssessmentData?.assessments || events.flatMap(event => 
        event.hazards.flatMap(hazard =>
          hazard.consequences.map(consequence => ({
            uniqueId: consequence.uniqueId,
            event: event.name,
            eventId: event.uniqueId,
            hazard: hazard.description,
            hazardId: hazard.uniqueId,
            consequence: consequence.description,
            consequenceId: consequence.uniqueId,
            currentControls: consequence.currentControls,
            probability: '',
            severity: '',
            likelihood: '',
            impact: '',
            tolerability: ''
          }))
        )
      )
    }
  });

  const watchAssessments = watch('assessments') || [];

  // Group assessments by event for display
  const groupedAssessments = watchAssessments.reduce((acc, assessment) => {
    if (!acc[assessment.event]) {
      acc[assessment.event] = {
        event: assessment.event,
        assessments: []
      };
    }
    acc[assessment.event].assessments.push(assessment);
    return acc;
  }, {});

  const handleFormSubmit = async (data) => {
    try {
      // Validate required fields
      const hasEmptyFields = data.assessments.some(assessment => 
        !assessment.probability || 
        !assessment.severity || 
        !assessment.likelihood || 
        !assessment.impact || 
        !assessment.tolerability
      );

      if (hasEmptyFields) {
        throw new Error('Please complete all risk assessment fields');
      }

      // Format the data
      const formattedData = {
        assessments: data.assessments.map(assessment => ({
          ...assessment,
          probability: parseInt(assessment.probability, 10),
          severity: assessment.severity,
          likelihood: parseInt(assessment.likelihood, 10),
          impact: parseInt(assessment.impact, 10),
          tolerability: assessment.tolerability
        }))
      };

      // Save to store
      const saved = setStepData(3, formattedData);
      if (!saved) {
        throw new Error('Failed to save risk assessment data');
      }

      // Save to database
      const savedToDb = await saveStepData(3);
      if (!savedToDb) {
        throw new Error('Failed to save risk assessment data to database');
      }

      // Proceed to next step
      setCurrentStep(4);
    } catch (err) {
      console.error('Error saving risk assessment:', err);
    }
  };

  // Show loading state
  if (isLoading) {
    return <LoadingSpinner />;
  }

  // Check if we have hazard data
  if (events.length === 0) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-white rounded-lg shadow-md p-6">
          <p className="text-gray-600 text-center">
            No hazards found. Please complete the Hazard Identification step first.
          </p>
          <div className="mt-4 flex justify-center">
            <button
              onClick={() => setCurrentStep(2)}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
            >
              Go to Hazard Identification
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Risk Assessment</h2>

        {error && <ErrorAlert message={error} />}

        {Object.keys(errors).length > 0 && (
          <ErrorAlert message="Please complete all required fields" />
        )}

        <RiskMatrixSelector />
        <RiskMatrixReference />
      </div>

      <AssessmentForm 
        groupedAssessments={groupedAssessments}
        setValue={setValue}
        onSubmit={handleSubmit(handleFormSubmit)}
        register={register}
        isLoading={isLoading}
      />
    </div>
  );
}
