import React from 'react';
import {
  PROBABILITY_VALUES,
  SEVERITY_VALUES,
  RISK_MATRIX,
  TOLERABILITY_COLORS,
  TOLERABILITY_ACTIONS,
  TOLERABILITY_INTOLERABLE,
  TOLERABILITY_TOLERABLE,
  TOLERABILITY_ACCEPTABLE,
} from '../constants/icaoMatrix';

/**
 * ICAORiskMatrix Component
 *
 * This component displays the ICAO Risk Matrix, allowing users to visualize
 * and understand the risk assessment based on probability and severity.
 * It also provides information about the tolerability levels and corresponding actions.
 */
const ICAORiskMatrix = () => {
  /**
   * Determines the background color class based on the tolerability level.
   * @param {string} tolerability - The tolerability level ('INTOLERABLE', 'TOLERABLE', 'ACCEPTABLE').
   * @returns {string} The Tailwind CSS class for the background color.
   */
  const getTolerabilityColor = (tolerability) => {
    return TOLERABILITY_COLORS[tolerability] || 'bg-gray-100';
  };

  /**
   * Determines the action associated with a tolerability level.
   * @param {string} tolerability - The tolerability level ('INTOLERABLE', 'TOLERABLE', 'ACCEPTABLE').
   * @returns {string} The action corresponding to the tolerability level.
   */
  const getTolerabilityAction = (tolerability) => {
    switch (tolerability) {
      case TOLERABILITY_INTOLERABLE:
        return TOLERABILITY_ACTIONS[TOLERABILITY_INTOLERABLE];
      case TOLERABILITY_TOLERABLE:
        return TOLERABILITY_ACTIONS[TOLERABILITY_TOLERABLE];
      case TOLERABILITY_ACCEPTABLE:
        return TOLERABILITY_ACTIONS[TOLERABILITY_ACCEPTABLE];
      default:
        return 'Unknown Action';
    }
  };

  return (
    <div className="icao-risk-matrix">
      <h3 className="text-lg font-bold mb-4">ICAO Risk Matrix</h3>
      <table className="w-full border-collapse table-auto">
        <thead>
          <tr>
            <th className="border px-4 py-2">Probability \ Severity</th>
            {Object.values(SEVERITY_VALUES).map((severity) => (
              <th key={severity.value} className="border px-4 py-2">
                {severity.label} ({severity.value})
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Object.values(PROBABILITY_VALUES).map((probability) => (
            <tr key={probability.value}>
              <td className="border px-4 py-2">
                {probability.label} ({probability.value})
              </td>
              {Object.values(SEVERITY_VALUES).map((severity) => {
                const riskIndex = `${probability.value}${severity.value}`;
                const tolerability = RISK_MATRIX[riskIndex];
                return (
                  <td
                    key={severity.value}
                    className={`border px-4 py-2 text-center ${getTolerabilityColor(
                      tolerability
                    )}`}
                  >
                    {riskIndex}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>

      <div className="mt-4">
        <h4 className="font-bold mb-2">Tolerability Levels:</h4>
        <ul>
          <li
            className={`font-semibold ${getTolerabilityColor(
              TOLERABILITY_INTOLERABLE
            )}`}
          >
            {TOLERABILITY_INTOLERABLE}:{' '}
            {getTolerabilityAction(TOLERABILITY_INTOLERABLE)}
          </li>
          <li
            className={`font-semibold ${getTolerabilityColor(
              TOLERABILITY_TOLERABLE
            )}`}
          >
            {TOLERABILITY_TOLERABLE}:{' '}
            {getTolerabilityAction(TOLERABILITY_TOLERABLE)}
          </li>
          <li
            className={`font-semibold ${getTolerabilityColor(
              TOLERABILITY_ACCEPTABLE
            )}`}
          >
            {TOLERABILITY_ACCEPTABLE}:{' '}
            {getTolerabilityAction(TOLERABILITY_ACCEPTABLE)}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default ICAORiskMatrix;
