import { supabase } from './supabase';

export async function createAssessments(projectId, assessmentsData, matrixType) {
  console.log('Creating assessments for project:', projectId);
  
  const assessmentPromises = assessmentsData.assessments.map(async assessment => {
    try {
      // Verify the consequence exists
      const { data: consequence, error: consequenceError } = await supabase
        .from('consequences')
        .select('id')
        .eq('id', assessment.consequenceId)
        .single();

      if (consequenceError) {
        console.error('Error finding consequence:', consequenceError);
        throw new Error(`Consequence not found: ${assessment.consequenceId}`);
      }

      // Create the risk assessment
      const { data: createdAssessment, error: assessmentError } = await supabase
        .from('risk_assessments')
        .insert({
          consequence_id: consequence.id,
          matrix_type: matrixType, // Add matrix type
          probability: assessment.probability ? parseInt(assessment.probability) : null,
          severity: assessment.severity || null,
          likelihood: assessment.likelihood ? parseInt(assessment.likelihood) : null,
          impact: assessment.impact ? parseInt(assessment.impact) : null,
          tolerability: assessment.tolerability || null
        })
        .select(`
          id,
          matrix_type,
          probability,
          severity,
          likelihood,
          impact,
          tolerability,
          consequence:consequences!inner(
            id,
            description,
            current_controls,
            hazard:hazards!inner(
              id,
              description,
              event:events!inner(
                id,
                name
              )
            )
          )
        `)
        .single();

      if (assessmentError) {
        console.error('Error creating assessment:', assessmentError);
        throw assessmentError;
      }

      return {
        uniqueId: createdAssessment.id,
        event: createdAssessment.consequence.hazard.event.name,
        hazard: createdAssessment.consequence.hazard.description,
        consequence: createdAssessment.consequence.description,
        currentControls: createdAssessment.consequence.current_controls,
        matrixType: createdAssessment.matrix_type,
        probability: createdAssessment.probability,
        severity: createdAssessment.severity,
        likelihood: createdAssessment.likelihood,
        impact: createdAssessment.impact,
        tolerability: createdAssessment.tolerability
      };
    } catch (error) {
      console.error('Error processing assessment:', error);
      throw error;
    }
  });

  try {
    const results = await Promise.all(assessmentPromises);
    console.log('Successfully created all assessments:', results);
    return { assessments: results };
  } catch (error) {
    console.error('Error creating assessments:', error);
    throw error;
  }
}

export async function updateAssessments(projectId, assessmentsData, matrixType) {
  try {
    // First get all existing assessments for this project through the relationships
    const { data: existingAssessments, error: fetchError } = await supabase
      .from('risk_assessments')
      .select(`
        id,
        consequence:consequences!inner(
          hazard:hazards!inner(
            event:events!inner(
              project_id
            )
          )
        )
      `)
      .eq('consequence.hazard.event.project_id', projectId);

    if (fetchError) throw fetchError;

    // Delete existing assessments if any exist
    if (existingAssessments?.length > 0) {
      const { error: deleteError } = await supabase
        .from('risk_assessments')
        .delete()
        .in('id', existingAssessments.map(a => a.id));

      if (deleteError) throw deleteError;
    }

    // Create new assessments with matrix type
    return createAssessments(projectId, assessmentsData, matrixType);
  } catch (error) {
    console.error('Error updating assessments:', error);
    throw error;
  }
}

export async function getAssessmentsByProjectId(projectId) {
  try {
    const { data, error } = await supabase
      .from('risk_assessments')
      .select(`
        id,
        matrix_type,
        probability,
        severity,
        likelihood,
        impact,
        tolerability,
        consequence:consequences!inner(
          id,
          description,
          current_controls,
          hazard:hazards!inner(
            id,
            description,
            event:events!inner(
              id,
              name,
              project_id
            )
          )
        )
      `)
      .eq('consequence.hazard.event.project_id', projectId);

    if (error) throw error;

    return data.map(assessment => ({
      uniqueId: assessment.id,
      event: assessment.consequence.hazard.event.name,
      hazard: assessment.consequence.hazard.description,
      consequence: assessment.consequence.description,
      currentControls: assessment.consequence.current_controls,
      matrixType: assessment.matrix_type,
      probability: assessment.probability,
      severity: assessment.severity,
      likelihood: assessment.likelihood,
      impact: assessment.impact,
      tolerability: assessment.tolerability
    }));
  } catch (error) {
    console.error('Error getting assessments:', error);
    throw error;
  }
}
