import { supabase } from './supabase';

export async function createRiskControl(assessmentId, controlData) {
  try {
    const { data, error } = await supabase
      .from('hira_risk_controls')
      .insert({
        assessment_id: assessmentId,
        additional_mitigation: controlData.additionalMitigation,
        risk_owner: controlData.riskOwner,
        target_date: controlData.targetDate,
        date_implemented: controlData.dateImplemented,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .select()
      .single();

    if (error) throw error;
    return data;
  } catch (error) {
    console.error('Error creating risk control:', error);
    throw error;
  }
}

export async function updateRiskControl(controlId, controlData) {
  try {
    const { data, error } = await supabase
      .from('hira_risk_controls')
      .update({
        additional_mitigation: controlData.additionalMitigation,
        risk_owner: controlData.riskOwner,
        target_date: controlData.targetDate,
        date_implemented: controlData.dateImplemented,
        updated_at: new Date().toISOString()
      })
      .eq('id', controlId)
      .select()
      .single();

    if (error) throw error;
    return data;
  } catch (error) {
    console.error('Error updating risk control:', error);
    throw error;
  }
}

export async function getRiskControlByAssessmentId(assessmentId) {
  try {
    const { data, error } = await supabase
      .from('hira_risk_controls')
      .select(`
        id,
        assessment_id,
        additional_mitigation,
        risk_owner,
        target_date,
        date_implemented
      `)
      .eq('assessment_id', assessmentId)
      .single();

    if (error && error.code !== 'PGRST116') { // Ignore "not found" error
      throw error;
    }

    return data;
  } catch (error) {
    console.error('Error getting risk control:', error);
    throw error;
  }
}

export async function deleteRiskControl(controlId) {
  try {
    const { error } = await supabase
      .from('hira_risk_controls')
      .delete()
      .eq('id', controlId);

    if (error) throw error;
  } catch (error) {
    console.error('Error deleting risk control:', error);
    throw error;
  }
}
