import { createContext, useContext } from 'react';
import { create } from 'zustand';
import { supabase } from '../services/supabase';
import { projectService } from '../services/projectService';

// Custom error class for project operations
class ProjectOperationError extends Error {
  constructor(message, operation, details = null) {
    super(message);
    this.name = 'ProjectOperationError';
    this.operation = operation;
    this.details = details;
  }
}

const useProjectStore = create((set, get) => ({
  // State
  projects: [],
  currentProject: null,
  currentStep: 0,
  isLoading: false,
  error: null,
  retryCount: 0,
  maxRetries: 3,

  // Form Data State
  projectDetails: null,
  hazardIdentificationData: null,
  riskAssessmentData: null,
  riskControlsData: null,

  // Loading and Error Handlers
  setLoading: (isLoading) => set({ isLoading }),
  setError: (error) => set({ error }),
  clearError: () => set({ error: null }),
  
  // Reset state
  resetState: () => set({
    currentProject: null,
    currentStep: 0,
    projectDetails: null,
    hazardIdentificationData: null,
    riskAssessmentData: null,
    riskControlsData: null,
    error: null,
    retryCount: 0
  }),

  // Project Selection
  setCurrentProject: async (project) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      if (!project) {
        // Reset state for new project
        store.resetState();
        return;
      }

      // Set current project and project details
      set({ 
        currentProject: project,
        projectDetails: {
          projectId: project.projectId,
          title: project.title,
          date: project.date,
          facilitator: project.facilitator,
          attendees: project.attendees || [],
          operational_desc: project.operational_desc || '',
          operational_files: Array.isArray(project.operational_files) ? project.operational_files : []
        }
      });

      // Load project data from relational tables
      try {
        const projectData = await projectService.loadProjectData(project.id);
        if (projectData) {
          // Set hazard identification data
          set({ hazardIdentificationData: { events: projectData } });

          // Set the current step based on available data
          const lastCompletedStep = projectData.length > 0 ? 2 : 1;
          set({ currentStep: Math.min(lastCompletedStep + 1, 4) });
        }
      } catch (error) {
        console.error('Error loading project data:', error);
        // Don't throw, just log the error and continue
      }

    } catch (error) {
      console.error('Error setting current project:', error);
      store.setError(`Failed to load project data: ${error.message}`);
    } finally {
      store.setLoading(false);
    }
  },

  // Navigation
  setCurrentStep: (step) => {
    const store = get();
    
    // Always allow navigation to step 0 (dashboard) or 1 (project details)
    if (step <= 1) {
      set({ currentStep: step, error: null });
      return true;
    }

    // For other steps, check prerequisites
    const previousStepData = store.getStepData(step - 1);
    if (!previousStepData) {
      store.setError('Please complete the previous step first');
      return false;
    }

    set({ currentStep: step, error: null });
    return true;
  },

  // Project Operations with Retry Logic
  loadProjects: async () => {
    const store = get();
    store.setLoading(true);
    store.clearError();
    
    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw new ProjectOperationError('Failed to load projects', 'LOAD', error);

      set({ projects: data });
    } catch (error) {
      console.error('Load projects error:', error);
      if (store.retryCount < store.maxRetries) {
        set(state => ({ retryCount: state.retryCount + 1 }));
        await store.loadProjects();
      } else {
        set({ 
          error: `Failed to load projects after ${store.maxRetries} attempts: ${error.message}`,
          retryCount: 0
        });
      }
    } finally {
      store.setLoading(false);
    }
  },

  createProject: async (projectData) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .insert([projectData])
        .select()
        .single();

      if (error) throw new ProjectOperationError('Failed to create project', 'CREATE', error);

      set(state => ({
        projects: [data, ...state.projects],
        currentProject: data,
        projectDetails: data,
        currentStep: 2 // Automatically advance to Hazard Identification
      }));

      return data;
    } catch (error) {
      console.error('Create project error:', error);
      set({ error: `Failed to create project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  updateProject: async (id, projectData) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .update(projectData)
        .eq('id', id)
        .select()
        .single();

      if (error) throw new ProjectOperationError('Failed to update project', 'UPDATE', error);

      set(state => ({
        projects: state.projects.map(p => p.id === id ? data : p),
        currentProject: data,
        projectDetails: data
      }));

      return data;
    } catch (error) {
      console.error('Update project error:', error);
      set({ error: `Failed to update project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  deleteProject: async (id) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      // Use projectService to delete all related data
      await projectService.deleteProjectData(id);

      // Then delete the project
      const { error } = await supabase
        .from('hira_projects')
        .delete()
        .eq('id', id);

      if (error) throw new ProjectOperationError('Failed to delete project', 'DELETE', error);

      set(state => ({
        projects: state.projects.filter(p => p.id !== id),
        ...(state.currentProject?.id === id ? {
          currentProject: null,
          projectDetails: null,
          currentStep: 0
        } : {})
      }));
    } catch (error) {
      console.error('Delete project error:', error);
      set({ error: `Failed to delete project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  // Form Data Management
  setStepData: async (step, data) => {
    const store = get();
    try {
      // Update local state
      switch (step) {
        case 1:
          set({ projectDetails: data });
          break;
        case 2:
          set({ hazardIdentificationData: data });
          // Save to relational tables
          if (store.currentProject?.id) {
            for (const event of data.events) {
              await projectService.saveEvent(store.currentProject.id, event);
            }
          }
          break;
        case 3:
          set({ riskAssessmentData: data });
          break;
        case 4:
          set({ riskControlsData: data });
          break;
        default:
          throw new Error(`Invalid step: ${step}`);
      }
      return true;
    } catch (error) {
      console.error(`Error in setStepData(${step}):`, error);
      store.setError(`Failed to save step ${step} data: ${error.message}`);
      return false;
    }
  },

  getStepData: (step) => {
    const state = get();
    switch (step) {
      case 1:
        return state.projectDetails;
      case 2:
        return state.hazardIdentificationData;
      case 3:
        return state.riskAssessmentData;
      case 4:
        return state.riskControlsData;
      default:
        return null;
    }
  }
}));

const ProjectStoreContext = createContext();

export const ProjectStoreProvider = ({ children }) => {
  const store = useProjectStore();
  return (
    <ProjectStoreContext.Provider value={store}>
      {children}
    </ProjectStoreContext.Provider>
  );
};

export const useProjectStoreContext = () => useContext(ProjectStoreContext);

export default useProjectStore;
