import { createContext, useContext } from 'react';
import { create } from 'zustand';
import { supabase } from '../services/supabase';

// Custom error class for project operations
class ProjectOperationError extends Error {
  constructor(message, operation, details = null) {
    super(message);
    this.name = 'ProjectOperationError';
    this.operation = operation;
    this.details = details;
  }
}

const useProjectStore = create((set, get) => ({
  // State
  projects: [],
  currentProject: null,
  currentStep: 0,
  isLoading: false,
  error: null,
  retryCount: 0,
  maxRetries: 3,

  // Form Data State (merged from formStore)
  projectDetails: null,
  hazardIdentificationData: null,
  riskAssessmentData: null,
  riskControlsData: null,

  // Loading and Error Handlers
  setLoading: (isLoading) => set({ isLoading }),
  setError: (error) => set({ error }),
  clearError: () => set({ error: null }),
  
  // Reset state
  resetState: () => set({
    currentProject: null,
    currentStep: 0,
    projectDetails: null,
    hazardIdentificationData: null,
    riskAssessmentData: null,
    riskControlsData: null,
    error: null,
    retryCount: 0
  }),

  // Project Selection
  setCurrentProject: async (project) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      if (!project) {
        // Reset state for new project
        store.resetState();
        return;
      }

      // Set current project
      set({ 
        currentProject: project,
        projectDetails: {
          projectId: project.projectId,
          title: project.title,
          date: project.date,
          facilitator: project.facilitator,
          attendees: project.attendees || [],
          operational_desc: project.operational_desc || '',
          operational_files: Array.isArray(project.operational_files) ? project.operational_files : []
        }
      });

      // Load associated data
      await Promise.all([
        store.loadStepData(2), // Hazard Identification
        store.loadStepData(3), // Risk Assessment
        store.loadStepData(4)  // Risk Controls
      ]);

    } catch (error) {
      console.error('Error setting current project:', error);
      store.setError(`Failed to load project data: ${error.message}`);
    } finally {
      store.setLoading(false);
    }
  },

  // Navigation
  setCurrentStep: (step) => {
    const currentData = get().getStepData(step);
    if (step > 0 && !currentData) {
      set({ error: 'Please complete the previous step first' });
      return false;
    }
    set({ currentStep: step, error: null });
    return true;
  },

  // Project Operations with Retry Logic
  loadProjects: async () => {
    const store = get();
    store.setLoading(true);
    store.clearError();
    
    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw new ProjectOperationError('Failed to load projects', 'LOAD', error);

      set({ projects: data });
    } catch (error) {
      console.error('Load projects error:', error);
      if (store.retryCount < store.maxRetries) {
        set(state => ({ retryCount: state.retryCount + 1 }));
        await store.loadProjects();
      } else {
        set({ 
          error: `Failed to load projects after ${store.maxRetries} attempts: ${error.message}`,
          retryCount: 0
        });
      }
    } finally {
      store.setLoading(false);
    }
  },

  createProject: async (projectData) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .insert([projectData])
        .select()
        .single();

      if (error) throw new ProjectOperationError('Failed to create project', 'CREATE', error);

      set(state => ({
        projects: [data, ...state.projects],
        currentProject: data,
        projectDetails: data
      }));

      return data;
    } catch (error) {
      console.error('Create project error:', error);
      set({ error: `Failed to create project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  updateProject: async (id, projectData) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .update(projectData)
        .eq('id', id)
        .select()
        .single();

      if (error) throw new ProjectOperationError('Failed to update project', 'UPDATE', error);

      set(state => ({
        projects: state.projects.map(p => p.id === id ? data : p),
        currentProject: data,
        projectDetails: data
      }));

      return data;
    } catch (error) {
      console.error('Update project error:', error);
      set({ error: `Failed to update project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  deleteProject: async (id) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const { error } = await supabase
        .from('hira_projects')
        .delete()
        .eq('id', id);

      if (error) throw new ProjectOperationError('Failed to delete project', 'DELETE', error);

      set(state => ({
        projects: state.projects.filter(p => p.id !== id),
        currentProject: state.currentProject?.id === id ? null : state.currentProject,
        projectDetails: state.currentProject?.id === id ? null : state.projectDetails
      }));
    } catch (error) {
      console.error('Delete project error:', error);
      set({ error: `Failed to delete project: ${error.message}` });
      throw error;
    } finally {
      store.setLoading(false);
    }
  },

  // Form Data Management
  setStepData: (step, data) => {
    const store = get();
    try {
      switch (step) {
        case 1:
          set({ projectDetails: data });
          break;
        case 2:
          set({ hazardIdentificationData: data });
          break;
        case 3:
          set({ riskAssessmentData: data });
          break;
        case 4:
          set({ riskControlsData: data });
          break;
        default:
          throw new Error(`Invalid step: ${step}`);
      }
      return true;
    } catch (error) {
      store.setError(`Failed to save step ${step} data: ${error.message}`);
      return false;
    }
  },

  getStepData: (step) => {
    const state = get();
    switch (step) {
      case 1:
        return state.projectDetails;
      case 2:
        return state.hazardIdentificationData;
      case 3:
        return state.riskAssessmentData;
      case 4:
        return state.riskControlsData;
      default:
        return null;
    }
  },

  // Save step data to database
  saveStepData: async (step) => {
    const store = get();
    const data = store.getStepData(step);
    if (!data) return false;

    store.setLoading(true);
    store.clearError();

    try {
      const projectId = store.currentProject?.id;
      if (!projectId) throw new Error('No current project');

      const { error } = await supabase
        .from('hira_project_data')
        .upsert({
          project_id: projectId,
          step,
          data,
          updated_at: new Date().toISOString()
        });

      if (error) throw new ProjectOperationError(`Failed to save step ${step}`, 'SAVE_STEP', error);

      return true;
    } catch (error) {
      console.error(`Save step ${step} error:`, error);
      store.setError(`Failed to save step ${step}: ${error.message}`);
      return false;
    } finally {
      store.setLoading(false);
    }
  },

  // Load step data from database
  loadStepData: async (step) => {
    const store = get();
    store.setLoading(true);
    store.clearError();

    try {
      const projectId = store.currentProject?.id;
      if (!projectId) throw new Error('No current project');

      const { data, error } = await supabase
        .from('hira_project_data')
        .select('data')
        .eq('project_id', projectId)
        .eq('step', step)
        .single();

      if (error) throw new ProjectOperationError(`Failed to load step ${step}`, 'LOAD_STEP', error);

      if (data) {
        store.setStepData(step, data.data);
        return true;
      }
      return false;
    } catch (error) {
      console.error(`Load step ${step} error:`, error);
      store.setError(`Failed to load step ${step}: ${error.message}`);
      return false;
    } finally {
      store.setLoading(false);
    }
  }
}));

const ProjectStoreContext = createContext();

export const ProjectStoreProvider = ({ children }) => {
  const store = useProjectStore();
  return (
    <ProjectStoreContext.Provider value={store}>
      {children}
    </ProjectStoreContext.Provider>
  );
};

export const useProjectStoreContext = () => useContext(ProjectStoreContext);

export default useProjectStore;
