import React from 'react';
import useProjectStore from '../stores/projectStore';
import { useRiskMatrixStore } from '../stores/riskMatrixStore';
import RiskMatrixSelector from './RiskMatrixSelector';
import AssessmentForm from './AssessmentForm';
import FloatingMatrix from './FloatingMatrix';

const RiskAssessment = () => {
  const { currentProject, hazardIdentificationData } = useProjectStore();
  const { matrixType, setMatrixType } = useRiskMatrixStore();

  if (!currentProject || !hazardIdentificationData) {
    return <div>No data available</div>;
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Risk Assessment</h2>

      {/* Matrix Type Selection */}
      <div className="mb-6">
        <RiskMatrixSelector 
          matrixType={matrixType} 
          setMatrixType={setMatrixType}
        />
      </div>

      {/* Assessment Form */}
      <AssessmentForm 
        events={hazardIdentificationData.events || []}
        matrixType={matrixType}
      />

      {/* Floating Matrix */}
      <FloatingMatrix />
    </div>
  );
};

export default RiskAssessment;
