import { create } from 'zustand';
import { supabase } from '../services/supabase';
import { listFiles } from '../services/supabase';

const useProjectStore = create((set, get) => ({
  projects: [],
  currentProject: null,
  currentStep: 0,
  isLoading: false,
  error: null,

  setCurrentProject: async (project) => {
    try {
      // Load files for the project
      if (project?.project_id) {
        const files = await listFiles(project.project_id);
        project = {
          ...project,
          operational_files: files
        };
      }
      set({ currentProject: project });
    } catch (error) {
      console.error('Error loading project files:', error);
      set({ currentProject: project });
    }
  },

  setCurrentStep: (step) => set({ currentStep: step }),

  loadProjects: async () => {
    set({ isLoading: true, error: null });
    try {
      const { data, error } = await supabase
        .from('hira_projects')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Load files for each project
      const projectsWithFiles = await Promise.all(data.map(async (project) => {
        if (project.project_id) {
          const files = await listFiles(project.project_id);
          return {
            ...project,
            operational_files: files
          };
        }
        return project;
      }));

      set({ projects: projectsWithFiles });
    } catch (error) {
      console.error('Load projects error:', error);
      set({ error: error.message });
    } finally {
      set({ isLoading: false });
    }
  },

  createProject: async (projectData) => {
    set({ isLoading: true, error: null });
    try {
      // Ensure project_id exists
      if (!projectData.project_id) {
        throw new Error('Project ID is required');
      }

      const { data, error } = await supabase
        .from('hira_projects')
        .insert([{
          project_id: projectData.project_id,
          title: projectData.title,
          date: projectData.date,
          facilitator: projectData.facilitator,
          attendees: projectData.attendees,
          operational_desc: projectData.operational_desc,
          operational_files: projectData.operational_files?.map(file => ({
            name: file.name,
            path: file.path,
            url: file.url,
            size: file.size,
            type: file.type,
            project_id: projectData.project_id
          })) || []
        }])
        .select()
        .single();

      if (error) throw error;

      // Load files after creating project
      const files = await listFiles(data.project_id);
      const projectWithFiles = {
        ...data,
        operational_files: files
      };

      set((state) => ({
        projects: [projectWithFiles, ...state.projects],
        currentProject: projectWithFiles
      }));

      return projectWithFiles;
    } catch (error) {
      console.error('Create project error:', error);
      set({ error: error.message });
      throw error;
    } finally {
      set({ isLoading: false });
    }
  },

  updateProject: async (id, projectData) => {
    set({ isLoading: true, error: null });
    try {
      // Ensure project_id exists
      if (!projectData.project_id) {
        throw new Error('Project ID is required');
      }

      const { data, error } = await supabase
        .from('hira_projects')
        .update({
          project_id: projectData.project_id,
          title: projectData.title,
          date: projectData.date,
          facilitator: projectData.facilitator,
          attendees: projectData.attendees,
          operational_desc: projectData.operational_desc,
          operational_files: projectData.operational_files?.map(file => ({
            name: file.name,
            path: file.path,
            url: file.url,
            size: file.size,
            type: file.type,
            project_id: projectData.project_id
          })) || []
        })
        .eq('id', id)
        .select()
        .single();

      if (error) throw error;

      // Load files after updating project
      const files = await listFiles(data.project_id);
      const projectWithFiles = {
        ...data,
        operational_files: files
      };

      set((state) => ({
        projects: state.projects.map((p) =>
          p.id === id ? projectWithFiles : p
        ),
        currentProject: projectWithFiles
      }));

      return projectWithFiles;
    } catch (error) {
      console.error('Update project error:', error);
      set({ error: error.message });
      throw error;
    } finally {
      set({ isLoading: false });
    }
  },

  deleteProject: async (id) => {
    set({ isLoading: true, error: null });
    try {
      const { error } = await supabase
        .from('hira_projects')
        .delete()
        .eq('id', id);

      if (error) throw error;

      set((state) => ({
        projects: state.projects.filter((p) => p.id !== id),
        currentProject: null
      }));
    } catch (error) {
      console.error('Delete project error:', error);
      set({ error: error.message });
      throw error;
    } finally {
      set({ isLoading: false });
    }
  }
}));

export default useProjectStore;
