import { create } from 'zustand';

const useRiskMatrixStore = create((set) => ({
  // State
  matrixType: 'icao', // Default to ICAO matrix

  // Actions
  setMatrixType: (type) => {
    if (type !== 'icao' && type !== 'integrated') {
      console.error('Invalid matrix type:', type);
      return;
    }
    set({ matrixType: type });
  },
}));

export default useRiskMatrixStore;
